import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';

class PersonCard extends Component {
    render() {
        const {firstName, lastName , age , hairColor} = this.props; 
        return (
            <>
            <div className='d-flex justify-content-center'>
            <div className='col-6 bg-light m-4 p-5 rounded'>
                <h1 className='mb-4'><span className="text-info">{firstName}, {lastName}</span></h1>
                <h5 className="text-dark">Age : <span className="text-info">{age}</span></h5>
                <h6 className="text-">Hair Color : <span className="text-info">{hairColor}</span></h6>
            </div>
            </div>
            </>
        )
    }
}
    
export default PersonCard;
